function quadrado(n){return n**2}function cubo(n){return n**3}function fibonacci(n){if(n<=0)return"Ø";if(1===n)return 0;if(2===n)return 1;let t,r,u=0,e=1;for(let o=2;o<n;o++)r=e,e=t=u+e,u=r;return t}function fatorial(n){if(n<0)return"Ø";if(0===n)return 1;for(var t=n,r=n-1;r>0;r--)t*=r;return t}function cleanInput(){document.getElementById("input").value=""}function raiz(n){return n<0?"∈ C":(n**.5).toFixed(3)}var counter=1;function output(){numero=parseInt(document.getElementById("input").value);var n=document.getElementsByTagName("tbody")[0];1===counter&&(n.innerHTML="",counter++),cleanInput(),n.innerHTML+=`<tr>\n <td>${numero}</td>\n <td class="quadrado">${quadrado(numero)}</td>\n      <td class="cubo">${cubo(numero)}</td>\n<td class="fib">${fibonacci(numero)}</td>\n<td class="fatorial">${fatorial(numero)}</td>\n<td class="raiz">${raiz(numero)}</td>\n</tr>`}