var votar=0,menores=0,presos=0,aposentar=0,prioridade=0;function exibir(){for(var e=document.querySelector(".idades"),o=document.getElementById("idade").value,s=String(o).split(" "),r=0;r<s.length;r++)s[r]<16&&(menores+=1),s[r]>16&&(votar+=1),s[r]>18&&(presos+=1),s[r]>65&&(aposentar+=1),s[r]>80&&(prioridade+=1);e.innerHTML=`<p style="color:pink">Pessoas com idade menor que 16 anos: ${menores} </p>`,e.innerHTML+=`<p style="color:blue">Pessoas que já podem votar (+16 anos): ${votar} </p>`,e.innerHTML+=`<p style="color:gray">Pessoas que já podem ser presas (+18 anos): ${presos} </p>`,e.innerHTML+=`<p style="color:red"><strong>Pessoas que já podem se aposentar (+65 anos): ${aposentar}</strong></p>`,e.innerHTML+=`<p style="color:yellow">Pessoas que possuem alta prioridade em filas (+80 anos): ${prioridade}</p>`}