var names=[];var aleatory=[];var button=document.querySelector("button");counter=10;function getNames(){var objectNames={nome:"",numeroVogais:0};objectNames.nome=document.querySelector("#nome").value;for(j=0;j<objectNames.nome.length;j+=1){if(objectNames.nome[j].includes("a")==true||objectNames.nome[j].includes("A")==true){objectNames.numeroVogais+=1}if(objectNames.nome[j].includes("e")==true||objectNames.nome[j].includes("E")==true){objectNames.numeroVogais+=1}if(objectNames.nome[j].includes("i")==true||objectNames.nome[j].includes("I")==true){objectNames.numeroVogais+=1}if(objectNames.nome[j].includes("o")==true||objectNames.nome[j].includes("O")==true){objectNames.numeroVogais+=1}if(objectNames.nome[j].includes("u")==true||objectNames.nome[j].includes("U")==true){objectNames.numeroVogais+=1}}counter-=1;var information__counter=document.getElementById("information__counter");information__counter.innerHTML=`Restam ${ counter } nomes`;if(counter==1){information__counter.innerHTML=`Resta ${ counter } nome`}if(counter<=0){information__counter.innerHTML=``}names.unshift(objectNames);aleatory.push(objectNames.nome);for(var i=aleatory.length-1;i>0;i-=1){var j=Math.floor(Math.random()*(i+1));[aleatory[i],aleatory[j]]=[aleatory[j],aleatory[i]]}console.log(names);if(names.length==10){button__delete.innerHTML="Deletar tabela";document.getElementById("contador").innerHTML="";counter=10;var table=document.getElementsByTagName("table")[0];var tbody=table.getElementsByTagName("tbody")[0];tbody.innerHTML="";for(var i=0;i<names.length;i+=1){tbody.innerHTML+="<tr><td>"+names[i].nome+"</td><td>"+names[i].numeroVogais+"</td><td>"+aleatory[i]+"</td></tr>"}}}function deleteNames(){for(var c=0;c<10;c+=1){names.pop()}console.log(names);var table=document.getElementsByTagName("table")[0];var tbody=table.getElementsByTagName("tbody")[0];tbody.innerHTML=""}